import React from "react";
import { List as NMSList } from "@mui/material";
import PropTypes from "prop-types";

const List = ({ children, component, subheader, sx, fullWidth, ...props }) => {
  return (
    <NMSList
      component={component}
      subheader={subheader}
      sx={sx}
      fullWidth={fullWidth}
      {...props}
    >
      {children}
    </NMSList>
  );
};

List.propTypes = {
  children: PropTypes.node,
  component: PropTypes.elementType,
  subheader: PropTypes.node,
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
};

List.defaultProps = {
  children: "NMS List",
};

export default List;
