import React from "react";
import { ListItemSecondaryAction as NMSListItemSecondaryAction } from "@mui/material";
import PropTypes from "prop-types";

const ListItemSecondaryAction = ({ children, sx, ...props }) => {
  return (
    <NMSListItemSecondaryAction sx={sx} {...props}>
      {children}
    </NMSListItemSecondaryAction>
  );
};

ListItemSecondaryAction.propTypes = {
  children: PropTypes.node,
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
};

ListItemSecondaryAction.defaultProps = {
  children: "NMS ListItemSecondaryAction",
};

export default ListItemSecondaryAction;
