import React from "react";
import ReactQuill from "react-quill";

const RichText = ({
  id,
  className,
  value,
  defaultValue,
  placeholder,
  modules,
  formats,
  style,
  theme,
  children,
  onChange,
  onChangeSelection,
  ...props
}) => {
  return (
    <ReactQuill
      id={id}
      className={className}
      value={value}
      defaultValue={defaultValue}
      placeholder={placeholder}
      modules={modules}
      formats={formats}
      style={style}
      theme={theme}
      onChange={onChange}
      onChangeSelection={onChangeSelection}
      {...props}
    />
  );
};

export default RichText;
