import React from "react";
import { Dialog as NMSDialog, Box, IconButton } from "@mui/material";
import PropTypes from "prop-types";
import * as SolarIconSet from "solar-icon-set";

const Dialog = ({
  children,
  color,
  onClose,
  open,
  scroll,
  fullWidth,
  fullScreen,
  ...props
}) => {
  return (
    <NMSDialog
      onClose={onClose}
      open={open}
      scroll={scroll}
      fullWidth={fullWidth}
      fullScreen={fullScreen}
      {...props}
    >
      {onClose && (
        <Box sx={{ display: "flex", justifyContent: "end" }}>
          <IconButton edge="end" onClick={onClose} aria-label="close">
            <SolarIconSet.CloseCircle size={24} iconStyle="BoldDuotone" />
          </IconButton>
        </Box>
      )}

      {children}
    </NMSDialog>
  );
};

Dialog.propTypes = {
  children: PropTypes.node,
  color: PropTypes.oneOfType([
    PropTypes.oneOf([
      "inherit",
      "default",
      "primary",
      "secondary",
      "error",
      "info",
      "success",
      "warning",
    ]),
    PropTypes.string,
  ]),
  onClose: PropTypes.func,
  open: PropTypes.bool.isRequired,
};

export default Dialog;
