import React from "react";
import { CircularProgress as NMSCircularProgress } from "@mui/material";

const CircularProgress = ({
  children,
  color,
  disableShrink,
  size,
  sx,
  thickness,
  value,
  variant,
  label,
  ...props
}) => {
  return (
    <NMSCircularProgress
      color={color}
      disableShrink={disableShrink}
      size={size}
      sx={sx}
      thickness={thickness}
      value={value}
      variant={variant}
      label={label}
      {...props}
    >
      {children}
    </NMSCircularProgress>
  );
};

export default CircularProgress;
