import React from "react";
import { LinearProgress as NMSLinearProgress } from "@mui/material";

const LinearProgress = ({ children, color, sx, value, variant, ...props }) => {
  return (
    <NMSLinearProgress
      color={color}
      sx={sx}
      value={value}
      variant={variant}
      {...props}
    />
  );
};

export default LinearProgress;
