import React from "react";
import { SnackbarContent as NMSSnackbarContent } from "notistack";
import { Alert } from "@mui/material";

const SnackbarContent = ({
  children,
  color,
  message,
  action,
  severity,
  icon,
  autoHideDuration,
  variant,
  ...otherProps
}) => {
  return (
    <NMSSnackbarContent {...otherProps} autoHideDuration={autoHideDuration}>
      <Alert
        severity={severity}
        color={color}
        action={action}
        icon={icon}
        variant={variant}
      >
        {message} {children}
      </Alert>
    </NMSSnackbarContent>
  );
};

export default SnackbarContent;
