import React from "react";
import PropTypes from "prop-types";
import { Icon as NMSIcon } from "@mui/material";

const Icon = ({
  children,
  classes,
  color,
  sx,
  solariconset,
  iconStyle,
  size,
}) => {
  const SolarIcon = solariconset;

  return (
    <NMSIcon classes={classes} color={color} sx={{sx, display: "flex", alignContent: "center", justifyContent: "center"}}>
      {children ||
        (SolarIcon && <SolarIcon iconStyle={iconStyle} size={size}/>)}
    </NMSIcon>
  );
};

Icon.propTypes = {
  children: PropTypes.node,
  classes: PropTypes.object,
  color: PropTypes.oneOfType([
    PropTypes.oneOf([
      "inherit",
      "action",
      "disabled",
      "primary",
      "secondary",
      "error",
      "info",
      "success",
      "warning",
    ]),
    PropTypes.string,
  ]),

  sx: PropTypes.arrayOf(PropTypes.func),
  solariconset: PropTypes.object,
  iconStyle: PropTypes.string,

};

Icon.defaultProps = {
  fontSize: "medium",
};

export default Icon;
