import React from "react";
import Paper from "../../../components/Surfaces/Paper";

const CustPaper = (props) => {
  const { children, sx, darkMode } = props;
  return (
    <Paper
      sx={{
        sx,
        width: "100%",
        padding: { desktop: "48px", tablet: "24px", mobile: "16px" },
        backgroundColor: (theme) =>
        theme.palette.mode === "dark" ? "#212B36" : "#fff",
      }}
      elevation={4}
    >
      {children}
    </Paper>
  );
};

export default CustPaper;
