import { IconButton } from "@mui/material";
import * as SolarIconSet from "solar-icon-set";
import React from "react";


const CustModeButton = ({ darkMode, onClick }) => {
  return (
    <IconButton
      onClick={onClick}
      aria-label="toggle dark/light mode"
      sx={{ boxShadow: "1px 2px 4px 0px rgba(132, 149, 177, 0.10)" }}
    >
      {darkMode ? <SolarIconSet.MoonStars size={24} iconStyle="BoldDuotone" /> : <SolarIconSet.Sun size={24} iconStyle="BoldDuotone" />}
    </IconButton>
  );
};

export default CustModeButton;