import React from "react";
import { IconButton as NMSIconButton } from "@mui/material";
import PropTypes from "prop-types";

const IconButton = ({ children, color, edge, size, sx, ...props }) => {
  return (
    <NMSIconButton color={color} edge={edge} size={size} sx={sx} {...props}>
      {children}
    </NMSIconButton>
  );
};

IconButton.propTypes = {
  children: PropTypes.node,
  color: PropTypes.oneOfType([
    PropTypes.oneOf([
      "inherit",
      "default",
      "primary",
      "secondary",
      "error",
      "info",
      "success",
      "warning",
    ]),
    PropTypes.string,
  ]),
  edge: PropTypes.oneOfType([PropTypes.oneOf(["end", "start", false])]),
  size: PropTypes.oneOfType([
    PropTypes.oneOf(["small", "medium", "large"]),
    PropTypes.string,
  ]),
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
  ...NMSIconButton.PropTypes,
};

IconButton.defaultProps = {
  children: "NMS Icon Button",
  color: "default",
  size: "medium",
};

export default IconButton;
