import React from "react";
import { Checkbox as NMSCheckbox } from "@mui/material";
import PropTypes from "prop-types";

const Checkbox = ({ children, color, sx, label, labelPlacement, ...props }) => {
  return (
    <NMSCheckbox
      color={color}
      sx={sx}
      label={label}
      labelPlacement={labelPlacement}
      {...props}
    >
      {children}
    </NMSCheckbox>
  );
};

Checkbox.propTypes = {
  children: PropTypes.node,
  color: PropTypes.oneOfType([
    PropTypes.oneOf([
      "default",
      "primary",
      "secondary",
      "error",
      "info",
      "success",
      "warning",
    ]),
    PropTypes.string,
  ]),
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
  ...NMSCheckbox.propTypes,
};

Checkbox.defaultProps = {
  children: "NMS Checkbox",
  color: "primary",
};

export default Checkbox;
