import React from "react";
import { FormControl as NMSFormControl } from "@mui/material";

const FormControl = ({
  children,
  color,
  component,
  margin,
  size,
  sx,
  variant,
  ...props
}) => {
  return (
    <NMSFormControl
      color={color}
      component={component}
      margin={margin}
      size={size}
      sx={sx}
      variant={variant}
      {...props}
    >
      {children}
    </NMSFormControl>
  );
};

export default FormControl;
