import React from "react";
import { FormControlLabel as NMSFormControlLabel } from "@mui/material";

const FormControlLabel = ({
  control,
  label,
  labelPlacement,
  onChange,
  sx,
  value,
  ...props
}) => {
  return (
    <NMSFormControlLabel
      control={control}
      label={label}
      labelPlacement={labelPlacement}
      onChange={onChange}
      sx={sx}
      value={value}
      {...props}
    />
  );
};

export default FormControlLabel;
