import React from "react";
import { FormHelperText as NMSFormHelperText } from "@mui/material";

const FormHelperText = ({
  children,
  component,
  margin,
  sx,
  variant,
  ...props
}) => {
  return (
    <NMSFormHelperText
      component={component}
      margin={margin}
      sx={sx}
      variant={variant}
      {...props}
    >
      {children}
    </NMSFormHelperText>
  );
};

export default FormHelperText;
