import React from "react";
import { Radio as NMSRadio, FormControlLabel } from "@mui/material";
import PropTypes from "prop-types";

const Radio = ({ children, label, value, checked, onChange, sx, color, ...props }) => {
  return (
    <NMSRadio
      // control={<NMSRadio />}
      label={label}
      value={value}
      checked={checked}
      onChange={onChange}
      sx={sx}
      color={color}
      {...props}
    >{children}</NMSRadio>
  );
};

Radio.propTypes = {
  label: PropTypes.node,
  value: PropTypes.string,
  checked: PropTypes.bool,
  onChange: PropTypes.func,
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
  ...NMSRadio.propTypes,
};

export default Radio;
