import React, { Fragment } from "react";

import { Select as NMSSelect, InputLabel } from "@mui/material";

const Select = ({
  children,
  fullWidth,
  labelId,
  id,
  value,
  label,
  onChange,
  options,
  variant,
  autowidth,
  size,
  ...props
}) => {
  return (
    <Fragment>
      <InputLabel>{label}</InputLabel>
      <NMSSelect
        labelId={labelId}
        children={children}
        fullWidth={fullWidth}
        id={id}
        value={value}
        label={label}
        onChange={onChange}
        options={options}
        props={props}
        variant={variant}
        autowidth={autowidth}
        size={size}
      >
        {children}
      </NMSSelect>
    </Fragment>
  );
};

export default Select;
