import React from "react";
import { Container as NMSContainer } from "@mui/material";
import PropTypes from "prop-types";

const Container = ({ children, component, maxWidth, sx, ...props }) => {
  return (
    <NMSContainer component={component} maxWidth={maxWidth} sx={sx} {...props}>
      {children}
    </NMSContainer>
  );
};

Container.propTypes = {
  children: PropTypes.node,
  component: PropTypes.elementType,
  maxWidth: PropTypes.oneOfType([
    PropTypes.oneOf(["mobile", "tablet", "laptop", "desktop", "largeDesktop"]),
    PropTypes.bool,
    PropTypes.string,
  ]),
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
  ...NMSContainer.PropTypes,
};

export default Container;
