import React from "react";
import { Stack as NMSStack } from "@mui/material";
import PropTypes from "prop-types";

const Stack = ({ children, component, direction, spacing, sx, ...props }) => {
  return (
    <NMSStack
      component={component}
      direction={direction}
      spacing={spacing}
      sx={sx}
      {...props}
    >
      {children}
    </NMSStack>
  );
};

Stack.propTypes = {
  children: PropTypes.node,
  component: PropTypes.elementType,
  direction: PropTypes.oneOfType([
    PropTypes.oneOf(["column-reverse", "column", "row-reverse", "row"]),
    PropTypes.arrayOf(
      PropTypes.oneOf(["column-reverse", "column", "row-reverse", "row"])
    ),
    PropTypes.object,
  ]),
  spacing: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
};

export default Stack;
