import React from "react";
import { DayCalendarSkeleton as NMSDayCalendarSkeleton } from "@mui/x-date-pickers";
import PropTypes from "prop-types";

const DayCalendarSkeleton = ({ sx, ...props }) => {
  return <NMSDayCalendarSkeleton sx={sx} {...props} />;
};

DayCalendarSkeleton.propTypes = {
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
};

export default DayCalendarSkeleton;
