import React from "react";
import { Tab as NMSTab } from "@mui/material";
import PropTypes from "prop-types";

const Tab = ({ icon, iconPosition, label, sx, value, ...props }) => {
  return (
    <NMSTab
      icon={icon}
      iconPosition={iconPosition}
      label={label}
      sx={sx}
      value={value}
      {...props}
    />
  );
};

Tab.propTypes = {
  icon: PropTypes.oneOfType([PropTypes.element, PropTypes.string]),
  iconPosition: PropTypes.oneOf(["bottom", "end", "start", "top"]),
  label: PropTypes.node,
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
  value: PropTypes.any,
  ...NMSTab.PropTypes,
};

Tab.defaultProps = {
  children: "NMS Tab",
  iconPosition: "top",
};

export default Tab;
