import React from "react";
import { AccordionSummary as NMSAccordionSummary } from "@mui/material";
import PropTypes from "prop-types";
import * as SolarIconSet from "solar-icon-set";

const AccordionSummary = ({ children, expandIcon, sx, ...props }) => {
  return (
    <NMSAccordionSummary expandIcon={expandIcon} sx={sx} {...props}>
      {children}
    </NMSAccordionSummary>
  );
};

AccordionSummary.propTypes = {
  children: PropTypes.node,
  expandIcon: PropTypes.node,
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
  ...NMSAccordionSummary.PropTypes,
};

AccordionSummary.defaultProps = {
  expandIcon: <SolarIconSet.AltArrowDown size={24} iconStyle="BoldDuotone" />,
};

export default AccordionSummary;
