import React from "react";
import { Card as NMSCard } from "@mui/material";
import PropTypes from "prop-types";

const Card = ({ clickable, children, sx, ...props }) => {
  return (
    <NMSCard clickable={clickable} sx={sx} {...props}>
      {children}
    </NMSCard>
  );
};

Card.propTypes = {
  children: PropTypes.node,
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
  ...NMSCard.PropTypes,
};

export default Card;
