import React from "react";
import { Paper as NMSPaper } from "@mui/material";
import PropTypes from "prop-types";

const Paper = ({ children, component, elevation, sx, variant, fullWidth,  ...props }) => {
  return (
    <NMSPaper
      component={component}
      elevation={elevation}
      sx={sx}
      variant={variant}
      fullWidth={fullWidth}
      {...props}
    >
      {children}
    </NMSPaper>
  );
};

Paper.propTypes = {
  children: PropTypes.node,
  component: PropTypes.elementType,
  elevation: PropTypes.number,
  sx: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.oneOfType([PropTypes.func, PropTypes.object, PropTypes.bool])
    ),
    PropTypes.func,
    PropTypes.object,
    PropTypes.bool,
  ]),
  variant: PropTypes.oneOfType([
    PropTypes.oneOf(["elevation", "outlined"]),
    PropTypes.string,
  ]),
};

Paper.defaultProps = {
  fullWidth: false,
  variant: "elevation",
  
};

export default Paper;
