export const NestedExample = `import React from "react";
import {
  Icon,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
} from "nms-creative-ui";
import * as SolarIconSet from "solar-icon-set";

import { Collapse } from "@mui/material";

const Nested = () => {
  const [open, setOpen] = React.useState(true);
  const [open2, setOpen2] = React.useState(true);

  const handleClick = () => {
    setOpen(!open);
  };
  const handleClick2 = () => {
    setOpen2(!open2);
  };

  return (
    <List>
      <ListItem>
        <ListItemButton onClick={handleClick}>
          <ListItemIcon>
            <Icon
              color={"secondary"}
              size={24}
              iconStyle={"BoldDuotone"}
              solariconset={SolarIconSet.Buildings}
            />
          </ListItemIcon>
          <ListItemText primary="Inbox" />
          {open ? (
            <Icon
              size={24}
              color={"secondary"}
              iconStyle={"BoldDuotone"}
              solariconset={SolarIconSet.AltArrowUp}
            />
          ) : (
            <Icon
              size={24}
              color={"secondary"}
              iconStyle={"BoldDuotone"}
              solariconset={SolarIconSet.AltArrowDown}
            />
          )}
        </ListItemButton>
      </ListItem>
      <Collapse in={open} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItemButton sx={{ pl: 10 }}>
            <ListItemText primary="Starred" />
          </ListItemButton>
          <ListItemButton sx={{ pl: 10 }}>
            <ListItemText primary="Starred" />
          </ListItemButton>
          <ListItemButton sx={{ pl: 10 }}>
            <ListItemText primary="Starred" />
          </ListItemButton>
        </List>
      </Collapse>

      <ListItem>
        <ListItemButton onClick={handleClick2}>
          <ListItemIcon>
            <Icon
              color={"secondary"}
              size={24}
              iconStyle={"BoldDuotone"}
              solariconset={SolarIconSet.Buildings}
            />
          </ListItemIcon>
          <ListItemText primary="Inbox" />
          {open ? (
            <Icon
              size={24}
              color={"secondary"}
              iconStyle={"BoldDuotone"}
              solariconset={SolarIconSet.AltArrowUp}
            />
          ) : (
            <Icon
              size={24}
              color={"secondary"}
              iconStyle={"BoldDuotone"}
              solariconset={SolarIconSet.AltArrowDown}
            />
          )}
        </ListItemButton>
      </ListItem>
      <Collapse in={open2} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItemButton sx={{ pl: 10 }}>
            <ListItemText primary="Starred" />
          </ListItemButton>
          <ListItemButton sx={{ pl: 10 }}>
            <ListItemText primary="Starred" />
          </ListItemButton>
          <ListItemButton sx={{ pl: 10 }}>
            <ListItemText primary="Starred" />
          </ListItemButton>
        </List>
      </Collapse>
    </List>
  );
};

export default Nested;`
