export const StickySubHeaderExample = `import React from "react";
import {
  List,
  ListItem,
  ListItemText,
  ListSubheader,
} from "nms-creative-ui";

const StickySubHeader = () => {
  return (
    <List
      sx={{
        position: "relative",
        overflow: "auto",
        maxHeight: 300,
        "& ul": { padding: 0 },
      }}
      subheader={<li />}
    >
      {[0, 1, 2, 3, 4].map((sectionId) => (
        <li key={\`section-'\${sectionId}'\`}>
          <ul>
            <ListSubheader
              sx={{ bgcolor: "#FBFBFC" }}
            >{\`I'm sticky '\${sectionId}'\`}</ListSubheader>
            {[0, 1, 2].map((item) => (
              <ListItem key={\`item-'\${sectionId}'-'\${item}'\`}>
                <ListItemText primary={\`Item '\${item}'\`} />
              </ListItem>
            ))}
          </ul>
        </li>
      ))}
    </List>
  );
};

export default StickySubHeader;`