import React from "react";
import {
  Icon,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
} from "../../../../../components";
import * as SolarIconSet from "solar-icon-set";
import { Collapse } from "@mui/material";

const BasicList = () => {
  const [open, setOpen] = React.useState(true);

  const handleClick = () => {
    setOpen(!open);
  };

  return (
    <List>
      <ListItem>
        <ListItemButton onClick={handleClick}>
          <ListItemIcon>
            <Icon
              color={"secondary"}
              size={24}
              iconStyle={"BoldDuotone"}
              solariconset={SolarIconSet.Buildings}
            />
          </ListItemIcon>
          <ListItemText primary="Inbox" />
          {open ? (
            <Icon
              size={24}
              solariconset={SolarIconSet.AltArrowUp}
              iconStyle={"BoldDuotone"}
              color={"secondary"}
            />
          ) : (
            <Icon
              size={24}
              solariconset={SolarIconSet.AltArrowDown}
              iconStyle={"BoldDuotone"}
              color={"secondary"}
            />
          )}
        </ListItemButton>
      </ListItem>
      <Collapse in={open} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItemButton sx={{ pl: 10 }}>
            <ListItemIcon>
              <Icon
                size={24}
                solariconset={SolarIconSet.Buildings}
                iconStyle={"BoldDuotone"}
                color={"secondary"}
              />
            </ListItemIcon>
            <ListItemText primary="Starred" />
          </ListItemButton>
        </List>
      </Collapse>
    </List>
  );
};

export default BasicList;
