import React from "react";
import {
  List,
  ListItem,
  ListItemText,
  Icon
} from "../../../../../components";
import * as SolarIconSet from "solar-icon-set";

const GutterLessList = () => {
  return (
    <List>
      {[1, 2, 3].map((value) => (
        <ListItem
          key={value}
          disableGutters
          secondaryAction={
            <Icon
              size={24}
              aria-label="comment"
              iconStyle={"BoldDuotone"}
              solariconset={SolarIconSet.Hourglass}
            />
          }
        >
          <ListItemText primary={`Line item ${value}`} />
        </ListItem>
      ))}
    </List>
  );
};

export default GutterLessList;
