export const TransitionDialogExample = `import React from "react";
import { Fragment } from "react";
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  List,
  ListItem,
} from "nms-creative-ui";
import { useState } from "react";
import { Slide } from "@mui/material";

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

const TransitionDialog = () => {
  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleCloseBasic = (value) => {
    setOpen(false);
  };
  return (
    <Fragment>
      <Button onClick={handleClickOpen}>Transition Dialog</Button>

      <Dialog
        fullWidth={false}
        open={open}
        onClick={handleCloseBasic}
        TransitionComponent={Transition}
      >
        <DialogTitle>Basic Transition Dialog</DialogTitle>
        <DialogContent>
          <List>
            <ListItem>Hello,</ListItem>
            <ListItem>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
            </ListItem>
          </List>
          <DialogActions>
          <Button variant={"text"}>cancel</Button>
            <Button>done</Button>
          </DialogActions>
        </DialogContent>
      </Dialog>
    </Fragment>
  );
};

export default TransitionDialog;`;
