export const BasicExample = `import React, { Fragment, useState } from "react";
import { Autocomplete, Grid } from "nms-creative-ui";

const Basic = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleChange = (event, value) => {
    setSelectedOption(value);
  };

  const getOptionLabel = (option) => (option && option.label) || "";

  const options = [
    {
      icon: "",
      label: "Option 1",
      value: 1,
    },
    {
      icon: "",
      label: "Option 2",
      value: 2,
    },
    {
      icon: "",
      label: "Option 3",
      value: 3,
    },
  ];
  return (
    <Fragment>
      <Grid container spacing={2}>
        <Grid item desktop={6}>
          <Autocomplete
            options={options}
            value={selectedOption}
            onChange={handleChange}
            label="Options"
            fullWidth
            getOptionLabel={getOptionLabel}
            renderInput={() => <TextField {...params} label="Choose a name" />}
          />
        </Grid>
        <Grid item desktop={6}>
          <Autocomplete
            label="Options"
            options={options}
            getOptionLabel={(option) => option.label}
            onChange={(event) => setSelectedOption(event.target.value)}
            startAdornment={
              selectedOption &&
              names.find((item) => item.value === selectedOption).icon
            }
          />
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default Basic;`