export const ControllableStatesExample = `import React, { useState } from "react";
import { Autocomplete, Box, Grid, Typography } from "nms-creative-ui";
import { FormControl } from "@mui/material";

const ControllableStates = () => {
  const options = [
    {
      icon: "",
      label: "Option 1",
      value: 1,
    },
    {
      icon: "",
      label: "Option 2",
      value: 2,
    },
    {
      icon: "",
      label: "Option 3",
      value: 3,
    },
  ];

  const [value, setValue] = useState(options[0]);
  const [inputValue, setInputValue] = useState("");

  return (
    <Grid container spacing={2}>
      <Grid item desktop={12}>
        <Box sx={{ display: "flex", justifyContent: "center", width: "100%" }}>
          <Typography>{\`inputValue: '\${inputValue}'\`}</Typography>
        </Box>
      </Grid>

      <Grid item desktop={12}>
        <Box sx={{ display: "flex", justifyContent: "center", width: "100%" }}>
          <FormControl sx={{ width: 300 }}>
            <Autocomplete
              label="Controllable"
              value={value}
              onChange={(event, newValue) => {
                setValue(newValue);
              }}
              inputValue={inputValue}
              onInputChange={(event, newInputValue) => {
                setInputValue(newInputValue);
              }}
              id="controllable"
              options={options}
              renderInput={(params) => <TextField {...params} />}
            />
          </FormControl>
        </Box>
      </Grid>
    </Grid>
  );
};

export default ControllableStates;
`