export const MultipleValueExample = `import React, { Fragment } from "react";
import { Autocomplete, Checkbox, Grid } from "nms-creative-ui";
import CheckBoxOutlineBlankIcon from "@mui/icons-material/CheckBoxOutlineBlank";
import CheckBoxIcon from "@mui/icons-material/CheckBox";

const MultipleValue = () => {
  const options = [
    {
      icon: "",
      label: "Option 1",
      value: 1,
    },
    {
      icon: "",
      label: "Option 2",
      value: 2,
    },
    {
      icon: "",
      label: "Option 3",
      value: 3,
    },
  ];

  const icon = <CheckBoxOutlineBlankIcon fontSize="small" />;
  const checkedIcon = <CheckBoxIcon fontSize="small" />;

  return (
    <Fragment>
      <Grid container spacing={2}>
        <Grid item desktop={12}>
          <Autocomplete
            label="Multiple Values"
            multiple
            id="checkboxes-tags"
            options={options}
            disableCloseOnSelect
            getOptionLabel={(option) => option.title}
            renderOption={(props, option, { selected }) => (
              <li
                {...props}
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <Checkbox
                  icon={icon}
                  checkedIcon={checkedIcon}
                  checked={selected}
                />
                {option.label}
              </li>
            )}
            renderInput={(params) => (
              <TextField label="Checkboxes" placeholder="hehe" />
            )}
          />
        </Grid>
      </Grid>
    </Fragment>
  );
};

export default MultipleValue;`;
