
import React, { useState } from "react";
import { Autocomplete, Stack, TextField, Typography } from "../../../../../components";

const options = ["Option 1", "Option 2"];

const ControllableStates = () => {
  const [value, setValue] = useState(options[0]);
  const [inputValue, setInputValue] = useState("");
  return (
    <Stack spacing={2}>
      <Typography>{`value: ${value !== null ? `'${value}'` : "null"}`}</Typography>
      <Typography>{`inputValue: '${inputValue}'`}</Typography>
      <br />
      <Autocomplete
        label="Controllable"
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
        inputValue={inputValue}
        onInputChange={(event, newInputValue) => {
          setInputValue(newInputValue);
        }}
        id="controllable-states-demo"
        options={options}
        renderInput={(params) => <TextField {...params} />}
      />
    </Stack>
  );
};

export default ControllableStates;
