import React, { useState } from "react";
import { Icon, Stack } from "../../../../../../components";
import {
  ToggleButton,
  ToggleButtonGroup,
} from "../../../../../../components/Input/ToggleButton";
import * as SolarIconSet from "solar-icon-set";

const Disabled = () => {
  const [formats, setFormats] = useState();

  const handleFormat = (event, newFormats) => {
    setFormats(newFormats);
  };

  return (
      <Stack direction="row" spacing={2}>
        <ToggleButtonGroup
          orientation="vertical"
          value={formats}
          exclusive
          onChange={handleFormat}
          aria-label="text formatting"
        >
          <ToggleButton value="bold" disabled aria-label="bold">
            <Icon
              solariconset={SolarIconSet.TextBold}
              size={24}
              color={"disabled"}
              iconStyle={"Outline"}
            />
          </ToggleButton>

          <ToggleButton value="italic" disabled aria-label="italic">
            <Icon
              solariconset={SolarIconSet.TextItalic}
              size={24}
              color={"disabled"}
              iconStyle={"Outline"}
            />
          </ToggleButton>

          <ToggleButton value="underlined" disabled aria-label="underlined">
            <Icon
              solariconset={SolarIconSet.TextUnderline}
              size={24}
              color={"disabled"}
              iconStyle={"Outline"}
            />
          </ToggleButton>

          <ToggleButton value="eraser" disabled aria-label="eraser">
            <Icon
              solariconset={SolarIconSet.Eraser}
              size={24}
              color={"disabled"}
              iconStyle={"Outline"}
            />
          </ToggleButton>
        </ToggleButtonGroup>
      </Stack>
  );
};

export default Disabled;
