import React, { Fragment, useState } from "react";
import {
  ToggleButton,
  ToggleButtonGroup,
} from "../../../../../../components/Input/ToggleButton";
import * as SolarIconSet from "solar-icon-set";
import { Icon } from "../../../../../../components";

const MultipleSelection = () => {
  const [formats, setFormats] = useState(() => ["bold", "underlined"]);

  const handleFormat = (event, newFormats) => {
    setFormats(newFormats);
  };

  return (
    <Fragment>
      <ToggleButtonGroup
        value={formats}
        onChange={handleFormat}
        aria-label="text formatting"
      >
        <ToggleButton value="bold" aria-label="bold">
          <Icon
            solariconset={SolarIconSet.TextBold}
            size={24}
            iconStyle={"Outline"}
          />
        </ToggleButton>

        <ToggleButton value="italic" aria-label="italic">
          <Icon
            solariconset={SolarIconSet.TextItalic}
            size={24}
            iconStyle={"Outline"}
          />
        </ToggleButton>

        <ToggleButton value="underlined" aria-label="underlined">
          <Icon
            solariconset={SolarIconSet.TextUnderline}
            size={24}
            iconStyle={"Outline"}
          />
        </ToggleButton>

        <ToggleButton value="eraser" aria-label="eraser">
          <Icon
            solariconset={SolarIconSet.Eraser}
            size={24}
            iconStyle={"Outline"}
          />
        </ToggleButton>
      </ToggleButtonGroup>
    </Fragment>
  );
};

export default MultipleSelection;
