import React, { useState } from "react";
import {
  ToggleButton,
  ToggleButtonGroup,
} from "../../../../../../components/Input/ToggleButton";
import * as SolarIconSet from "solar-icon-set";
import { Icon, Stack } from "../../../../../../components";

const Sizes = () => {
  const [formats, setFormats] = useState("bold");

  const handleFormat = (event, newFormats) => {
    setFormats(newFormats);
  };

  const children = [
    <ToggleButton value="bold" aria-label="bold">
      <Icon
        solariconset={SolarIconSet.TextBold}
        size={24}
        iconStyle={"Outline"}
      />
    </ToggleButton>,

    <ToggleButton value="italic" aria-label="italic">
      <Icon
        solariconset={SolarIconSet.TextItalic}
        size={24}
        iconStyle={"Outline"}
      />
    </ToggleButton>,

    <ToggleButton value="underlined" aria-label="underlined">
      <Icon
        solariconset={SolarIconSet.TextUnderline}
        size={24}
        iconStyle={"Outline"}
      />
    </ToggleButton>,

    <ToggleButton value="eraser" disabled aria-label="eraser">
      <Icon
        solariconset={SolarIconSet.Eraser}
        size={24}
        color={"disabled"}
        iconStyle={"Outline"}
      />
    </ToggleButton>,
  ];

  const control = {
    value: formats,
    onChange: handleFormat,
    exclusive: true,
  };

  return (
    <Stack direction="column" spacing={2}>
      <ToggleButtonGroup size="small" {...control} aria-label="Small sizes">
        {children}
      </ToggleButtonGroup>
      <ToggleButtonGroup {...control} aria-label="Medium sizes">
        {children}
      </ToggleButtonGroup>
      <ToggleButtonGroup size="large" {...control} aria-label="Large sizes">
        {children}
      </ToggleButtonGroup>
    </Stack>
  );
};

export default Sizes;
