export const ColorsExample = `import * as React from "react";
import {
  FormControl,
  FormControlLabel,
  FormLabel,
  Radio,
  RadioGroup,
} from "nms-creative-ui";

const Colors = () => {
  return (
    <FormControl>
      <FormLabel id="demo-radio-buttons-group-label">Colors</FormLabel>
      <RadioGroup
        aria-labelledby="demo-radio-buttons-group-label"
        name="radio-buttons-group"
      >
        <FormControlLabel control={<Radio color="primary" />} label="Primary" />
        <FormControlLabel control={<Radio />} label="Success" color="success" />
        <FormControlLabel control={<Radio color="warning" />} label="Warning" />
      </RadioGroup>
    </FormControl>
  );
};

export default Colors;`;
