export const ControlledExample = `import React, { useState } from "react";
import { RadioGroup } from "nms-creative-ui";

const Controlled = () => {
  const [controlledValue, setControlledValue] = useState("male");

  return (
    <RadioGroup
      name="myRadioGroup"
      value={controlledValue}
      onChange={setControlledValue}
      row
    >
      <RadioGroup.Radio label="Male" value="male" />
      <RadioGroup.Radio label="Female" value="female" />
    </RadioGroup>
  );
};

export default Controlled;`;
