export const DirectionExample = `import React, { useState } from "react";
import { RadioGroup } from "nms-creative-ui";

const Direction = () => {
  const [selectedValue, setSelectedValue] = useState();

  const handleChangeSelected = (event) => {
    setSelectedValue(event.target.value);
  };
  return (
    <RadioGroup
      name="myRadioGroup"
      value={selectedValue}
      onChange={handleChangeSelected}
      row
    >
      <RadioGroup label="Male" value="option1" />
      <RadioGroup label="Female" value="option2" />
      <RadioGroup label="Others" value="option3" />
    </RadioGroup>
  );
};

export default Direction;`;
