export const SizesExample = `import React, { useState } from "react";
import { RadioGroup } from "nms-creative-ui";

const Sizes = () => {
  const [sizeValue, setSizeValue] = useState("large");

  return (
    <RadioGroup value={sizeValue} onChange={setSizeValue} name="sizes" row>
      <RadioGroup size="small" label="Small" value="small" />
      <RadioGroup size="medium" label="Medium" value="medium" />
      <RadioGroup size="large" label="Large" value="large" />
    </RadioGroup>
  );
};

export default Sizes;`;
