export const ResponsibleExample = `import React, { Fragment } from 'react'
import { Paper, Stack } from 'nms-creative-ui';
import { styled } from '@mui/material/styles';

const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#638BCC' : '#FF4545',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.white.main,
  }));

const Responsible = () => {
    return (
        <Fragment>
          <Stack
            direction={{ mobile: 'column', tablet: 'row' }}
            spacing={{ mobile: 1, tablet: 2, laptop: 4 }}
          >
            <Item>Item 1</Item>
            <Item>Item 2</Item>
            <Item>Item 3</Item>
          </Stack>
        </Fragment>
    );
}

export default Responsible;`