import React from "react";
import { Box, Paper, Stack } from "../../../../../components";
import { styled } from '@mui/material/styles';

const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#638BCC' : '#FF4545',
    ...theme.typography.body2,
    "&.MuiPaper-elevation1": { borderRadius: "0px", padding: "10px" },
    textAlign: 'center',
    color: theme.palette.white.main,
  }));

const FlexboxGap = () => {
  return (
    <Box sx={{ width: 200 }}>
      <Stack
        spacing={{ mobile: 1, tablet: 2 }}
        direction="row"
        useFlexGap
        flexWrap="wrap"
      >
        <Item>Item 1</Item>
        <Item>Item 2</Item>
        <Item>Long content</Item>
      </Stack>
    </Box>
  );
};

export default FlexboxGap;