export const IconPositionExample = `import React from "react";
import * as SolarIconSet from "solar-icon-set";
import { Icon, Tab, Tabs } from "nms-creative-ui";

const IconPosition = () => {
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Tabs
      value={value}
      onChange={handleChange}
      aria-label="icon position tabs example"
    >
      <Tab
        icon={
          <Icon
            solariconset={SolarIconSet.PhoneRounded}
            size={24}
            iconStyle={"BoldDuotone"}
          />
        }
        label="top"
      />
      <Tab
        icon={
          <Icon
            solariconset={SolarIconSet.Heart}
            size={24}
            iconStyle={"BoldDuotone"}
          />
        }
        iconPosition="start"
        label="start"
      />
      <Tab
        icon={
          <Icon
            solariconset={SolarIconSet.ArchiveUpMinimlistic}
            size={24}
            iconStyle={"BoldDuotone"}
          />
        }
        iconPosition="end"
        label="end"
      />
      <Tab
        icon={
          <Icon
            solariconset={SolarIconSet.Settings}
            size={24}
            iconStyle={"BoldDuotone"}
          />
        }
        iconPosition="bottom"
        label="bottom"
      />
    </Tabs>
  );
};

export default IconPosition;`