export const SystemSample = `import React from "react";
import {
  Box,
  FormControl,
  FormControlLabel,
  Grid,
  Paper,
  Radio,
  RadioGroup,
  Stack,
  Typography,
} from "../../../../../components";
import { useState } from "react";

const System = () => {
  const [shadow, setShadow] = useState(1);

  const handleChange = (event) => {
    setShadow(Number(event.target.value));
  };

  return (
    <Grid sx={{ flexGrow: 1 }} container spacing={2}>
      <Stack direction={"column"} spacing={6} textAlign="center">
        <Typography variant={"h6"}>Shadow: {shadow}</Typography>
        <Grid item mobile={12}>
          <Grid container justifyContent="center" spacing={1}>
            {[0].map((value) => (
              <Grid key={value} item>
                <Paper
                  elevation={shadow}
                  sx={{
                    borderRadius: "20px",
                    padding: "48px",
                    width: "100%",
                    backgroundColor: (theme) =>
                      theme.palette.mode === "dark" ? "#1A2027" : "#fff",
                  }}
                >
                  {shadow}
                </Paper>
              </Grid>
            ))}
          </Grid>
        </Grid>
        <Grid item mobile={12}>
          <Grid container justifyContent="center">
            <Grid item>
              <FormControl component="fieldset">
                <RadioGroup
                  name="shadow"
                  aria-label="shadow"
                  value={shadow.toString()}
                  onChange={handleChange}
                  row
                >
                  {[1, 2, 3, 4, 6, 8, 12, 16, 24].map((value) => (
                    <FormControlLabel
                      key={value}
                      value={value.toString()}
                      control={<Radio />}
                      label={value.toString()}
                    />
                  ))}
                </RadioGroup>
              </FormControl>
            </Grid>
          </Grid>
        </Grid>
      </Stack>
    </Grid>
  );
};

export default System;
`;